svg.script:
function edgePos() {      const side = Math.floor(Math.random() * 4);      switch(side) {        case 0: return [Math.random() * 200, 0];        case 1: return [200, Math.random() * 100];        case 2: return [Math.random() * 200, 100];        case 3: return [0, Math.random() * 100];      }
}

function resetPath(id) {
  const element = document.getElementById(id);
  if (element) {
    const start = edgePos();
    const end = edgePos();
    const animateTransform = element.querySelector('animateTransform');
    if (animateTransform) {
      animateTransform.setAttribute('values', `${start[0]} ${start[1]}; ${end[0]} ${end[1]}`);
    }
  }
}

// Add mysterious randomness to movements
function addMystery() {
  const orbs = ['orb1', 'orb2'];
  orbs.forEach(orbId => {
    const orb = document.getElementById(orbId);
    if (orb && Math.random() > 0.7) {
      const opacity = Math.random() * 0.5 + 0.3;
      orb.style.opacity = opacity;
    }
  });
}

setInterval(addMystery, 8000);

// Enhanced cosmic phenomena
function triggerCosmicEvent() {
  const events = ['cosmicWave1', 'energyPulse'];
  const randomEvent = events[Math.floor(Math.random() * events.length)];
  const element = document.getElementById(randomEvent);
  if (element && Math.random() > 0.6) {
    element.style.opacity = Math.random() * 0.8 + 0.2;
  }
}

// Multi-dimensional depth effects
function createDepthEffect() {
  const depthElements = document.querySelectorAll('[filter="url(#depthBlur)"]');
  depthElements.forEach(element => {
    if (Math.random() > 0.85) {
      const scale = Math.random() * 0.3 + 0.85;
      element.style.transform = `scale(${scale})`;
      element.style.opacity = Math.random() * 0.4 + 0.3;
    }
  });
}

// Gentle stellar twinkling
function createTwinkling() {
  const stars = document.querySelectorAll('circle[fill="white"]');
  stars.forEach(star => {
    if (Math.random() > 0.95) {
      star.style.opacity = Math.random() * 0.3 + 0.7;
      setTimeout(() => {
        star.style.opacity = '';
      }, 1000);
    }
  });
}

setInterval(triggerCosmicEvent, 20000);
setInterval(createDepthEffect, 25000);
setInterval(createTwinkling, 5000);
