g.script:
// Modern immersive interactive system
let interactionState = {
  isInteracting: false,
  lastInteraction: 0,
  particleCount: 0
};

// Enhanced random positioning for dynamic elements
function generateRandomPosition() {
  const side = Math.floor(Math.random() * 4);
  switch(side) {
    case 0: return [Math.random() * 200, 0]; // top
    case 1: return [200, Math.random() * 100]; // right
    case 2: return [Math.random() * 200, 100]; // bottom
    case 3: return [0, Math.random() * 100]; // left
  }
}

// Dynamic particle creation for user interactions
function createInteractionParticle(x, y) {
  const svg = document.querySelector('svg');
  const particle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');

  particle.setAttribute('cx', x);
  particle.setAttribute('cy', y);
  particle.setAttribute('r', '0.5');
  particle.setAttribute('fill', `url(#quantumField)`);
  particle.setAttribute('opacity', '0.8');
  particle.setAttribute('filter', 'url(#quantumGlow)');
  particle.classList.add('cosmic-particle');

  // Animate particle expansion and fade
  const animate = document.createElementNS('http://www.w3.org/2000/svg', 'animate');
  animate.setAttribute('attributeName', 'r');
  animate.setAttribute('values', '0.5;2;0');
  animate.setAttribute('dur', '2s');
  animate.setAttribute('begin', '0s');

  const fadeOut = document.createElementNS('http://www.w3.org/2000/svg', 'animate');
  fadeOut.setAttribute('attributeName', 'opacity');
  fadeOut.setAttribute('values', '0.8;0.3;0');
  fadeOut.setAttribute('dur', '2s');
  fadeOut.setAttribute('begin', '0s');

  particle.appendChild(animate);
  particle.appendChild(fadeOut);
  svg.appendChild(particle);

  // Remove particle after animation
  setTimeout(() => {
    if (particle.parentNode) {
      particle.parentNode.removeChild(particle);
    }
  }, 2100);
}

// Enhanced cosmic phenomena with dynamic behavior
function triggerCosmicEvent() {
  const phenomena = ['quantumFluctuation', 'stellarFlare', 'energyWave'];
  const eventType = phenomena[Math.floor(Math.random() * phenomena.length)];

  switch(eventType) {
    case 'quantumFluctuation':
      const quantumElements = document.querySelectorAll('[filter*="quantumGlow"]');
      quantumElements.forEach(el => {
        el.style.opacity = Math.random() * 0.5 + 0.3;
        setTimeout(() => el.style.opacity = '', 2000);
      });
      break;

    case 'stellarFlare':
      const sun = document.getElementById('sun');
      if (sun) {
        const flares = sun.querySelectorAll('.sun-flare');
        flares.forEach(flare => {
          flare.style.opacity = '0.6';
          setTimeout(() => flare.style.opacity = '', 3000);
        });
      }
      break;

    case 'energyWave':
      const energyElements = document.querySelectorAll('[filter*="energyField"]');
      energyElements.forEach((el, index) => {
        setTimeout(() => {
          el.style.filter = 'url(#holographicShimmer)';
          setTimeout(() => el.style.filter = '', 1500);
        }, index * 200);
      });
      break;
  }
}

// Interactive hover effects for celestial bodies
function initializeInteractivity() {
  const interactiveElements = document.querySelectorAll('.interactive-element');

  interactiveElements.forEach(element => {
    element.addEventListener('mouseenter', function(e) {
      interactionState.isInteracting = true;
      interactionState.lastInteraction = Date.now();

      // Create interaction particle at cursor position
      const rect = element.getBoundingClientRect();
      const svgRect = document.querySelector('svg').getBoundingClientRect();
      const x = ((e.clientX - svgRect.left) / svgRect.width) * 200;
      const y = ((e.clientY - svgRect.top) / svgRect.height) * 100;

      createInteractionParticle(x, y);
    });

    element.addEventListener('mouseleave', function() {
      interactionState.isInteracting = false;
    });
  });
}

// Dynamic orbital path updates
function updateOrbitalPaths() {
  const orbitalTrails = document.querySelectorAll('.orbital-trail');
  orbitalTrails.forEach(trail => {
    const currentOffset = parseFloat(trail.style.strokeDashoffset) || 0;
    trail.style.strokeDashoffset = (currentOffset + 0.1) % 20;
  });
}

// Responsive scaling based on viewport
function updateResponsiveScaling() {
  const svg = document.querySelector('svg');
  const container = svg.parentElement;
  if (container) {
    const containerWidth = container.offsetWidth;
    if (containerWidth < 768) {
      svg.style.transform = 'scale(0.8)';
    } else if (containerWidth < 1024) {
      svg.style.transform = 'scale(0.9)';
    } else {
      svg.style.transform = 'scale(1)';
    }
  }
}

// Ambient particle generation
function generateAmbientParticles() {
  if (interactionState.particleCount < 3 && Math.random() > 0.98) {
    const pos = generateRandomPosition();
    createInteractionParticle(pos[0], pos[1]);
    interactionState.particleCount++;
    setTimeout(() => interactionState.particleCount--, 2100);
  }
}

// Main animation loop
function animationLoop() {
  updateOrbitalPaths();
  generateAmbientParticles();

  // Trigger random cosmic events
  if (Math.random() > 0.999) {
    triggerCosmicEvent();
  }

  requestAnimationFrame(animationLoop);
}

// Initialize when DOM is ready
document.addEventListener('DOMContentLoaded', function() {
  initializeInteractivity();
  updateResponsiveScaling();

  // Start animation loop
  animationLoop();

  // Handle window resize
  window.addEventListener('resize', updateResponsiveScaling);

  // Periodic cosmic events
  setInterval(triggerCosmicEvent, 15000 + Math.random() * 10000);
});

// Legacy compatibility
function edgePos() {
  return generateRandomPosition();
}

function addMystery() {
  triggerCosmicEvent();
}
